 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Persona } from '../../domain/auto-motor_db/persona';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../Persona.service.ts
 */

/*
 * SCHEMA DB Persona
 *
	{
		cognome: {
			type: 'String',
			required : true
		},
		eta: {
			type: 'Number'
		},
		mail: {
			type: 'String'
		},
		nome: {
			type: 'String',
			required : true
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		FK_PEROSNA_AUTO: {
			type: Schema.ObjectId,
			ref : "Persona"
		},
	}
 *
 */
@Injectable()
export class PersonaBaseService {

    contextUrl: string = environment.endpoint + '/personas';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * PersonaService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Persona): Observable<Persona> {
        return this.http
            .post<Persona>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * PersonaService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * PersonaService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id 
    *
    */
    get(id: string): Observable<Persona> {
        return this.http
            .get<Persona>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * PersonaService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<Persona[]> {
        return this.http
            .get<Persona[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * PersonaService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: Persona): Observable<Persona> {
        return this.http
            .post<Persona>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
